INSERT INTO `book` VALUES (482, '焚天魂主', 'http://www.xbiquge.la/files/article/image/14/14665/14665s.jpg', '一土成二木', '    与天斗、与地斗，都不过瘾，偏偏与人斗！仙道门派，人，妖，神，仙，魔，玄黄九州大世界的爱恨情仇，恩怨纠葛，仙界的争斗法力，世人眼中的邪恶功法《魂诀》，瑕疵必报的人性，男人与男人的诺言，刀与剑之间火焰，唯有焚天魂主。qq交流群：257015701，咱们一起聊聊。\n', 1, 'http://www.xbiquge.la/14/14665/', '', 152622, '2020-11-25 16:00:42', '2020-11-25 16:00:42', NULL, 1);
