INSERT INTO `book` VALUES (1359, '神豪之超级秒杀系统', 'http://www.xbiquge.la/files/article/image/32/32965/32965s.jpg', '青山紫云', '    萧遥获得了一个超级秒杀系统，从此过上了神豪般的生活，每天的生活就是买买买。    手机是九块九，电脑也是九块九。宝马是九块九，保时捷也是九块九。    飞机航母九块九，火箭飞船也是九块九。神豪秒杀系统在手，全世界九块九包邮送到家。    神豪秒杀系统在手，全世界九块九包邮', 3, 'http://www.xbiquge.la/32/32965/', '', 97864, '2020-11-25 16:50:00', '2020-11-25 16:50:00', NULL, 1);
INSERT INTO `book` VALUES (1360, '时代巨子', 'http://www.xbiquge.la/files/article/image/27/27390/27390s.jpg', '吹风的羊', '    重生回到高中年代，靠着领先世界20年的目光，这一世，会万分精彩。    时代因为我改变，历史由我来创造。', 3, 'http://www.xbiquge.la/27/27390/', '', 101580, '2020-11-25 16:50:04', '2020-11-25 16:50:04', NULL, 1);
INSERT INTO `book` VALUES (1361, '最佳影星', 'http://www.xbiquge.la/files/article/image/15/15105/15105s.jpg', '白色十三号', '    我要躺在钞票堆里打滚！我要跟一群美女开趴！我要做好莱坞巨星！\n', 3, 'http://www.xbiquge.la/15/15105/', '', 102599, '2020-11-25 16:50:07', '2020-11-25 16:50:07', NULL, 1);
INSERT INTO `book` VALUES (1362, '全职赘婿', 'http://www.xbiquge.la/files/article/image/31/31670/31670s.jpg', '房东老才', '    和女神上司结婚，入赘豪门，软饭从天而降，才二十出头就过上了混吃等死的生活。    你以为这很轻松？吃软饭可是一门技术活！', 3, 'http://www.xbiquge.la/31/31670/', '', 103851, '2020-11-25 16:50:09', '2020-11-25 16:50:09', NULL, 1);
