INSERT INTO `book` VALUES (1364, '我的尤物总裁老婆', 'http://www.xbiquge.la/files/article/image/21/21920/21920s.jpg', '必须火', '    【火爆畅销】他是战场狼王，身怀绝世武功，还会神奇医术，本想平静生活，然而却奉师命下山，不得不和冰山总裁订下有名无实的婚约……', 3, 'http://www.xbiquge.la/21/21920/', '', 109401, '2020-11-25 16:50:18', '2020-11-25 16:50:18', NULL, 1);
