INSERT INTO `book` VALUES (1373, '全球制造', 'http://www.xbiquge.la/files/article/image/39/39866/39866s.jpg', '二将', '    计算机系的陈序，在帮人修电脑的时候，无意间创造出一个智能生命，世界因此而改变。    写网文，一秒钟百万字。机器翻译，全球从此无障碍交流。自动驾驶，世界上再也没有女司机这个物种。    人造视网膜，让你从    “四眼仔”变成    “靓仔”。生命工程科学，解开基因封锁，长生不死不再是神话传说……', 3, 'http://www.xbiquge.la/39/39866/', '', 127828, '2020-11-25 16:50:52', '2020-11-25 16:50:52', NULL, 1);
