INSERT INTO `book` VALUES (1454, '重生俄罗斯土豪', 'http://www.xbiquge.la/files/article/image/30/30904/30904s.jpg', '海鲜吃扇贝', '    重生战斗民族富二代，开坦克碾压法拉利，拿机枪扫兰博基尼，玩RPG火箭炮炸游艇，用火焰喷射器烧劳斯莱斯。    家里养北极熊做宠物，养老虎当坐骑，日子过得潇洒又自在。', 3, 'http://www.xbiquge.la/30/30904/', '', 88266, '2020-11-25 16:54:49', '2020-11-25 16:54:49', NULL, 1);
INSERT INTO `book` VALUES (1455, '总裁校花赖上我', 'http://www.xbiquge.la/files/article/image/10/10308/10308s.jpg', '鱼人二代', '    “招聘帅哥……”\n    “我是来……”\n    “你来找工作啊，正好我家总裁缺一个男朋友！”\n    “不是，我是来找校花退婚的……”\n    超级大高手楚楠不远万里跑到明城退婚，结果一下火车就被抓去成了霸道美女总裁的冒充男友！于是高穷帅的楚大侠就走上了一条变身高富帅，发家致富努力退婚……幸福之路。\n', 3, 'http://www.xbiquge.la/10/10308/', '', 91649, '2020-11-25 16:54:53', '2020-11-25 16:54:53', NULL, 1);
