INSERT INTO `book` VALUES (1473, '超级鉴宝大宗师', 'http://www.xbiquge.la/files/article/image/31/31339/31339s.jpg', '后方高能', '    钱？我有！美女？不缺！地位？等等，地位是什么？在华夏国，我富可敌国，坐拥百分之八十的珍奇异宝，每天请安的美女如云，这算地位吗？', 3, 'http://www.xbiquge.la/31/31339/', '', 114837, '2020-11-25 16:55:32', '2020-11-25 16:55:32', NULL, 1);
